INSERT INTO `config_info` VALUES (14, 'seataServer.properties', 'DEFAULT_GROUP', '# 数据存储方式，db代表数据库\r\nstore.mode=db\r\nstore.db.datasource=druid\r\nstore.db.dbType=mysql\r\nstore.db.driverClassName=com.mysql.jdbc.Driver\r\nstore.db.url=jdbc:mysql://127.0.0.1:3306/seata?serverTimezone=UTC\r\nstore.db.user=root\r\nstore.db.password=root\r\nstore.db.minConn=5\r\nstore.db.maxConn=30\r\nstore.db.globalTable=global_table\r\nstore.db.branchTable=branch_table\r\nstore.db.queryLimit=100\r\nstore.db.lockTable=lock_table\r\nstore.db.maxWait=5000\r\n# 事务、日志等配置\r\nserver.recovery.committingRetryPeriod=1000\r\nserver.recovery.asynCommittingRetryPeriod=1000\r\nserver.recovery.rollbackingRetryPeriod=1000\r\nserver.recovery.timeoutRetryPeriod=1000\r\nserver.maxCommitRetryTimeout=-1\r\nserver.maxRollbackRetryTimeout=-1\r\nserver.rollbackRetryTimeoutUnlockEnable=false\r\nserver.undo.logSaveDays=7\r\nserver.undo.logDeletePeriod=86400000\r\n\r\n# 客户端与服务端传输方式\r\ntransport.serialization=seata\r\ntransport.compressor=none\r\n# 关闭metrics功能，提高性能\r\nmetrics.enabled=false\r\nmetrics.registryType=compact\r\nmetrics.exporterList=prometheus\r\nmetrics.exporterPrometheusPort=9898', 'd395ade06b1f6860e433d918c6a2e96f', '2021-11-23 02:19:27', '2021-11-23 02:19:27', NULL, '127.0.0.1', '', '', 'seata分布式事务配置', NULL, NULL, 'properties', NULL);
INSERT INTO `config_info` VALUES (15, 'mall-product-dev.yaml', 'DEFAULT_GROUP', 'spring: \n  # redis配置\n  redis:\n    host: 127.0.0.1\n    port: 6379\n\n  # 数据源配置\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      driver-class-name: com.mysql.cj.jdbc.Driver\n      username: root\n      password: root\n      url: jdbc:mysql://localhost:3306/mall-pms?serverTimezone=UTC\n      # 初始化大小，最小，最大\n      initial-size: 5\n      min-idle: 5\n      max-active: 20\n      # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位毫秒\n      time-between-eviction-runs-millis: 60000\n      # 配置一个连接在池中最小生存时间\n      min-evictable-idle-time-millis: 300000\n      validation-query: SELECT 1 FROM sys_user\n      test-while-idle: true\n      test-on-borrow: false\n      test-on-return: false\n#设置日志级别\n#logging:\n # level:\n  #  com.xiaofei.product: debug\n\n', '81771bcaf7c99e268d25672d7a631fa0', '2021-11-23 03:07:46', '2021-11-26 09:52:12', '', '127.0.0.1', '', '', 'mall-product开发环境配置', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (17, 'mall-product.yaml', 'DEFAULT_GROUP', 'spring:\n  cache:\n    type: redis\n    redis:\n      #指定缓存时间:传入一个毫秒数\n      time-to-live: 3600000\n      #设置key的前缀，如果指定了前缀，使用指定前缀，如果没有指定前缀，默认使用@Cacheable注解中name属性的值\n      #key-prefix=mall_:\n      #设置是否使用key的前缀，建议设置为true，但是不建议在配置文件中指定前缀，建议使用@Cacheable注解中的value作为前缀，用于区分，类别、商品等分区\n      use-key-prefix: true\n      #设置是否缓存空值，设置为true可以解决缓存穿透，默认为true\n      cache-null-values: true \n\n  #日期格式化\n  jackson:\n    date-format: yyyy-MM-dd HH:mm:ss\n\n# MyBatis-Plus的相关配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/**/*.xml\n  global-config:\n    # MP的逻辑删除\n    db-config:\n      id-type: auto\n      logic-delete-value: 0 #删除\n      logic-not-delete-value: 1 #没删除\n  configuration:\n    map-underscore-to-camel-case: true\n\npagehelper:\n  reasonable: true # 当该参数设置为 true 时，pageNum<=0 时会查询第一页\n  helper-dialect: mysql # 指定当前连接的是什么数据库，\n  rowBoundsWithCount: true # 当该参数设置为true时，使用 RowBounds 分页会进行 count 查询。\n  pageSizeZero: true # ，当该参数设置为 true 时，如果 pageSize=0 或者 RowBounds.limit = 0 就会查询出全部的结果（相当于没有执行分页查询，但是返回结果仍然是 Page 类型）。\n  auto-dialect: true\n\n#设置日志级别\n# logging:\n  # level:\n    # com.xiaofei.product: debug\n ', 'b52336bc2eb7002d8cc95f8b100cd6e5', '2021-11-23 03:39:27', '2021-11-23 03:47:50', '', '127.0.0.1', '', '', 'mall-product商品服务的共享配置', '', '', 'yaml', '');
