INSERT INTO `config_info` VALUES (35, 'mall-gateway.yaml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true #开启从注册中心动态创建路由的功能，利用微服务名进行路由\n      routes:\n        # 用户认证服务\n        - id: mall-auth\n          uri: lb://mall-auth\n          predicates:\n            - Path=/user/**\n\n        # 会员服务\n        - id: mall-member\n          uri: lb://mall-member\n          predicates:\n            - Path=/member/**\n\n        # 商品服务\n        - id: mall-product\n          uri: lb://mall-product\n          predicates:\n            - Path=/product/**\n\n        # 检索服务\n        - id: mall-elasticsearch\n          uri: lb://mall-elasticsearch\n          predicates:\n            - Path=/elasticsearch/**\n\n        # 购物车服务\n        - id: mall-cart\n          uri: lb://mall-cart\n          predicates:\n            - Path=/cart/**\n\n        # 购物车服务\n        - id: mall-order\n          uri: lb://mall-order\n          predicates:\n            - Path=/order/**\n        # 第三方服务：比如，阿里文件上传\n        - id: mall-third-part\n          uri: lb://mall-third-part\n          predicates:\n            - Path=/thirdpart/**', 'd392d8569903682cba42ea41a910d0ed', '2021-11-23 08:43:25', '2021-11-29 02:53:21', '', '127.0.0.1', '', '', 'mall-gateway前端网关', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (36, 'mall-auth.yaml', 'DEFAULT_GROUP', 'spring:\n  mail:\n    host: smtp.qq.com #服务器\n    default-encoding: UTF-8\n    port: 465\n    username: 1903078434@qq.com # 邮箱地址，对应host的主机\n    password: ssdtrtrlhakxcigf # 此处为邮箱**授权码**，qq独有\n    form: ${spring.mail.username} # 发送人\n    protocol: smtp\n    properties:\n      mail:\n        smtp:\n          ssl:\n            enable: true #需要配置ssl\n  cache:\n    type: redis\n    redis:\n      #指定缓存时间:传入一个毫秒数\n      time-to-live: 3600000\n      #设置key的前缀，如果指定了前缀，使用指定前缀，如果没有指定前缀，默认使用@Cacheable注解中name属性的值\n      #key-prefix=mall_:\n      #设置是否使用key的前缀，建议设置为true，但是不建议在配置文件中指定前缀，建议使用@Cacheable注解中的value作为前缀，用于区分，类别、商品等分区\n      use-key-prefix: true\n      #设置是否缓存空值，设置为true可以解决缓存穿透，默认为true\n      cache-null-values: true\n\n  #日期格式化\n  jackson:\n    date-format: yyyy-MM-dd HH:mm:ss\n\n# MyBatis-Plus的相关配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/**/*.xml\n  global-config:\n    # MP的逻辑删除\n    db-config:\n      id-type: auto\n      logic-delete-value: 0 #删除\n      logic-not-delete-value: 1 #没删除\n  configuration:\n    map-underscore-to-camel-case: true\n\npagehelper:\n  reasonable: true # 当该参数设置为 true 时，pageNum<=0 时会查询第一页\n  helper-dialect: mysql # 指定当前连接的是什么数据库，\n  rowBoundsWithCount: true # 当该参数设置为true时，使用 RowBounds 分页会进行 count 查询。\n  pageSizeZero: true # ，当该参数设置为 true 时，如果 pageSize=0 或者 RowBounds.limit = 0 就会查询出全部的结果（相当于没有执行分页查询，但是返回结果仍然是 Page 类型）。\n  auto-dialect: true\n\n#设置日志级别\nlogging:\n  level:\n    com.xiaofei.auth: info', '5924640fe4d283ff4f6d1b3e741e9c4e', '2021-11-23 08:55:24', '2021-11-29 02:53:55', '', '127.0.0.1', '', '', 'mall-auth认证服务', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (38, 'mall-auth-dev.yaml', 'DEFAULT_GROUP', 'spring: \n  # 数据源配置\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      driver-class-name: com.mysql.cj.jdbc.Driver\n      username: root\n      password: root\n      url: jdbc:mysql://localhost:3306/mall-ums?serverTimezone=UTC\n      #username: xiaofei_mall\n      #password: (Lf010613)\n      #url: jdbc:mysql://rm-uf62h8jflx22gj0ivqo.mysql.rds.aliyuncs.com:3306/mall_ums?characterEncoding=utf8&useSSL=false&serverTimezone=UTC&useUnicode=true&allowPublicKeyRetrieval=true\n      # 初始化大小，最小，最大\n      initial-size: 5\n      min-idle: 5\n      max-active: 20\n      # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位毫秒\n      time-between-eviction-runs-millis: 60000\n      # 配置一个连接在池中最小生存时间\n      min-evictable-idle-time-millis: 300000\n      validation-query: SELECT 1 FROM sys_user\n      test-while-idle: true\n      test-on-borrow: false\n      test-on-return: false \n  # redis配置\n  redis:\n    host: 127.0.0.1\n    port: 6379', 'dc1020d956d6d0130eb8774a27c70ec3', '2021-11-23 08:57:17', '2021-11-29 02:54:15', '', '127.0.0.1', '', '', 'mall-auth认证模块，开发环境配置', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (55, 'ruoyi-gateway.yml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 商品服务\n        - id: mall-product\n          uri: lb://mall-product\n          predicates:\n            - Path=/product/**\n\n        # 订单服务\n        - id: mall-order\n          uri: lb://mall-order\n          predicates:\n            - Path=/order/**\n\n        # 检索服务-转发到elastic search中\n        - id: mall-elasticsearch\n          uri: lb://mall-elasticsearch\n          predicates:\n            - Path=/elasticsearch/**\n\n        # 库存服务\n        - id: mall-ware\n          uri: lb://mall-ware\n          predicates:\n            - Path=/ware/**\n\n        # 会员服务\n        - id: mall-member\n          uri: lb://mall-member\n          predicates:\n            - Path=/member/**\n\n        # 第三方服务：比如，阿里文件上传\n        - id: mall-third-part\n          uri: lb://mall-third-part\n          predicates:\n            - Path=/thirdpart/**\n        # 认证中心\n        - id: ruoyi-auth\n          uri: lb://ruoyi-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 代码生成\n        - id: ruoyi-gen\n          uri: lb://ruoyi-gen\n          predicates:\n            - Path=/code/**\n          filters:\n            - StripPrefix=1\n        # 定时任务\n        - id: ruoyi-job\n          uri: lb://ruoyi-job\n          predicates:\n            - Path=/schedule/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: ruoyi-system\n          uri: lb://ruoyi-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 文件服务\n        - id: ruoyi-file\n          uri: lb://ruoyi-file\n          predicates:\n            - Path=/file/**\n          filters:\n            - StripPrefix=1\n\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: true\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /*/v2/api-docs\n      - /csrf\n      - /thirdpart/**', '6538fbc7bc434d266721ae14a7b51e70', '2021-11-29 02:30:27', '2021-11-29 02:30:51', '', '127.0.0.1', '', '', '网关模块', '', '', 'yaml', '');
