HH:mm:ss\r\n\r\n# MyBatis-Plus的相关配置\r\nmybatis-plus:\r\n  mapper-locations: classpath:/mapper/**/*.xml\r\n  global-config:\r\n    # MP的逻辑删除\r\n    db-config:\r\n      id-type: auto\r\n      logic-delete-value: 0 #删除\r\n      logic-not-delete-value: 1 #没删除\r\n  configuration:\r\n    map-underscore-to-camel-case: true\r\n\r\npagehelper:\r\n  reasonable: true # 当该参数设置为 true 时，pageNum<=0 时会查询第一页\r\n  helper-dialect: mysql # 指定当前连接的是什么数据库，\r\n  rowBoundsWithCount: true # 当该参数设置为true时，使用 RowBounds 分页会进行 count 查询。\r\n  pageSizeZero: true # ，当该参数设置为 true 时，如果 pageSize=0 或者 RowBounds.limit = 0 就会查询出全部的结果（相当于没有执行分页查询，但是返回结果仍然是 Page 类型）。\r\n  auto-dialect: true\r\n\r\n#设置日志级别\r\nlogging:\r\n  level:\r\n    com.xiaofei.auth: debug', '1faf070d1df9c3ee9e29db098e2d8d16', '2021-11-23 16:55:23', '2021-11-23 08:55:24', NULL, '127.0.0.1', 'I', '');
