INSERT INTO `his_config_info` VALUES (35, 90, 'mall-gateway.yaml', 'DEFAULT_GROUP', '', 'spring:\n  redis:\n    host: 127.0.0.1\n    port: 6379\n    password:\n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true #开启从注册中心动态创建路由的功能，利用微服务名进行路由\n      routes:\n        # 用户认证服务\n        - id: mall-auth\n          uri: lb://mall-auth\n          predicates:\n            - Path=/user/**\n\n        # 会员服务\n        - id: mall-member\n          uri: lb://mall-member\n          predicates:\n            - Path=/member/**\n\n        # 商品服务\n        - id: mall-product\n          uri: lb://mall-product\n          predicates:\n            - Path=/product/**\n\n        # 检索服务\n        - id: mall-elasticsearch\n          uri: lb://mall-elasticsearch\n          predicates:\n            - Path=/elasticsearch/**\n\n        # 购物车服务\n        - id: mall-cart\n          uri: lb://mall-cart\n          predicates:\n            - Path=/cart/**\n\n        # 购物车服务\n        - id: mall-order\n          uri: lb://mall-order\n          predicates:\n            - Path=/order/**\n        # 第三方服务：比如，阿里文件上传\n        - id: mall-third-part\n          uri: lb://mall-third-part\n          predicates:\n            - Path=/thirdpart/**', 'a8493a522164fad906fe1308273e7868', '2021-11-29 10:53:21', '2021-11-29 02:53:21', '', '127.0.0.1', 'U', '');
INSERT INTO `his_config_info` VALUES (36, 91, 'mall-auth.yaml', 'DEFAULT_GROUP', '', 'spring:\n  mail:\n    host: smtp.qq.com #服务器\n    default-encoding: UTF-8\n    port: 465\n    username: 1903078434@qq.com # 邮箱地址，对应host的主机\n    password: ssdtrtrlhakxcigf # 此处为邮箱**授权码**，qq独有\n    form: ${spring.mail.username} # 发送人\n    protocol: smtp\n    properties:\n      mail:\n        smtp:\n          ssl:\n            enable: true #需要配置ssl\n  cache:\n    type: redis\n    redis:\n      #指定缓存时间:传入一个毫秒数\n      time-to-live: 3600000\n      #设置key的前缀，如果指定了前缀，使用指定前缀，如果没有指定前缀，默认使用@Cacheable注解中name属性的值\n      #key-prefix=mall_:\n      #设置是否使用key的前缀，建议设置为true，但是不建议在配置文件中指定前缀，建议使用@Cacheable注解中的value作为前缀，用于区分，类别、商品等分区\n      use-key-prefix: true\n      #设置是否缓存空值，设置为true可以解决缓存穿透，默认为true\n      cache-null-values: true\n  # redis配置\n  redis:\n    host: 127.0.0.1\n    port: 6379\n\n  #日期格式化\n  jackson:\n    date-format: yyyy-MM-dd HH:mm:ss\n\n# MyBatis-Plus的相关配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/**/*.xml\n  global-config:\n    # MP的逻辑删除\n    db-config:\n      id-type: auto\n      logic-delete-value: 0 #删除\n      logic-not-delete-value: 1 #没删除\n  configuration:\n    map-underscore-to-camel-case: true\n\npagehelper:\n  reasonable: true # 当该参数设置为 true 时，pageNum<=0 时会查询第一页\n  helper-dialect: mysql # 指定当前连接的是什么数据库，\n  rowBoundsWithCount: true # 当该参数设置为true时，使用 RowBounds 分页会进行 count 查询。\n  pageSizeZero: true # ，当该参数设置为 true 时，如果 pageSize=0 或者 RowBounds.limit = 0 就会查询出全部的结果（相当于没有执行分页查询，但是返回结果仍然是 Page 类型）。\n  auto-dialect: true\n\n#设置日志级别\nlogging:\n  level:\n    com.xiaofei.auth: debug', '565db82f88336ceb2489608503db8cb8', '2021-11-29 10:53:54', '2021-11-29 02:53:55', '', '127.0.0.1', 'U', '');
